 -233.6666666667px hsl(95deg, 100%, 50%), 39px -180.6666666667px hsl(256deg, 100%, 50%), 217px 31.3333333333px hsl(68deg, 100%, 50%), -80px -399.6666666667px hsl(20deg, 100%, 50%), -124px 39.3333333333px hsl(204deg, 100%, 50%), 136px -195.6666666667px hsl(132deg, 100%, 50%), -30px -321.6666666667px hsl(111deg, 100%, 50%), 69px 63.3333333333px hsl(267deg, 100%, 50%), -57px -183.6666666667px hsl(148deg, 100%, 50%);
  }
}
