// Heading style and animation
.headingsContainer {
    width: 100%;
    height: 100%;
    .animationContainer {
        width: 100%;
        height: 100%;
        display: grid;
        grid-template-columns: repeat(3, 1fr);
        max-width: 800px;
        margin: 0 auto;
        justify-items: center;
        padding: 0em 1em;
        .headingContainer {
            margin: 0 auto;
            justify-content: center;
            align-self: center;
            h2 {
                color: var(--white);
                font-size: 7rem;
                letter-spacing: 0.4rem;
                text-shadow: 2px 2px 1px var(--red);
                cursor: default;
                z-index: 2;
                opacity: 0;
                span {
                    color: var(--red);
                    text-shadow: 2px 2px 1px var(--white), -2px -2px 1px var(--white);
                }
                @media screen and (max-width: 800px) {
                    & {
                        font-size: 15vmin;
                    }
                }
            }
            img {
                justify-items: center;
                align-self: center;
                transform: translateX(-5%);
                animation: arrowRightAnimation 5s infinite ease-in-out;
                z-index: -1;
                opacity: 0.6;
            }
            .shortArrow {
                display: none;
            }
            @media screen and (max-width: 600px) {
                & {
                    .longArrow {
                        display: none;
                    }
                    .shortArrow {
                        display: inline-block;
                    }
                }
            }
            .hideHeaderContentLeft {
                animation: pushHeadingRight 2s ease-out forwards;
            }
            .hideHeaderContentRight {
                animation: pushHeadingLeft 2s ease-out forwards;
                animation-delay: 0.5s;
            }
            @keyframes pushHeadingRight {
                from {
                    transform: translateX(-100%);
                    opacity: 0;
                }
                to {
                    transform: translateX(0%);
                    opacity: 1;
                }
            }
            @keyframes pushHeadingLeft {
                from {
                    transform: translateX(100%);
                    opacity: 0;
                }
                to {
                    transform: translateX(0%);
                    opacity: 1;
                }
            }
            @keyframes arrowRightAnimation {
                0% {
                    transform: translateX(-7%);
                    opacity: 0.6;
                } 
                50% {
                    transform: translateX(4%);
                    opacity: 1;
                } 
                100% {
                    transform: translateX(-7%);
                    opacity: 0.6;
                }
            }
        }
        .arrowContainer {
            z-index: -1;
        }
    }
}

