    --semiTransparentDarkgrey: rgb(23,23,23,0.9);
    --white: white;
    --black: black;
}

h1, h2, h3, h4 {
    font-family: var(--mainHeaderFont);
}

p {
    font-family: var(--paragraphFontFamily);
}

// Header Section -- Header Section -- Header Section 

.headerContainer {
    width: 100%;
    height: 100%;
    padding: 3em;
