            p {
                font-size: 1.2rem;
                font-family: var(--paragraphFontFamily);
                z-index: 5;
                max-width: 30em;
                cursor: default;
                span {
                    text-shadow: 1px 1px 1px var(--red), -1px -1px 1px var(--red);
                }
            }
        }
    }
    .ourServicesSection {
        min-height: 100vh;
        width: 100%;
        margin-top: 2em;
        z-index: 10;
        background-color: var(--semiTransparentDarkgrey);
        padding: 3em 1em;
        position: relative;
        border-top: 1px solid var(--white);
        .tabs {
            position: relative;
            display: grid;
            [role="tablist"] {
                display: grid;
                grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
                grid-gap: 10px;
            }
            [role="tabpanel"] {
                padding: 2rem;
            }
            .tablist {
                text-align: center;
                .tabSelectionButton {
                    border: 0;
                    color: var(--white);
                    border-radius: 5px;
                    box-shadow: inset 0 -3px 10px var(--black);
                    cursor: pointer;
                    height: 1em;
                    margin: 1em 2em;
                    font-family: var(--paragraphFontFamily);
                    padding: 1em;
                    padding-bottom: 2.5em;
                    background: linear-gradient(0deg, rgba(139,0,0,1) 73%, rgba(0,0,0,1) 100%);
                    transition: 0.5s;
                }
                .tabSelectionButton[aria-selected="true"] {
                    background: var(--red);
                    box-shadow: none;
                    color: var(--white);
                }

                .tabSelectionButton:focus {
                    outline: 5px;
                    color: var(--white);
                }
            }
            .tabPanelDiv {
                .tabPanelInfoContainer {
                    z-index: 10;
                    display: grid;
                    grid-template-columns: repeat(2, 1fr);
                    width: 100%;
                    @media screen and (max-width: 1000px) {
                        grid-template-columns: 1fr;
                        .tabPanelp {
                            grid-row-start: 1;
                        }
                    }
                    img {
                        width: 100%; 
                        object-fit: cover;

                    }
                    p {
                        color: var(--white);
                        width: 100%;
                        align-self: center;
                        font-size: 1.2rem;
                    }
                }
            }
        }
    }
}

// footer
.footerContainer {
    background-color: var(--semiTransparentDarkgrey);
    .footerPanel {
        min-height: 50vh;
        background: var(--black);
        border-top: 1px var(--white) solid;
        border-right: 1px var(--white) solid;
        border-left: 1px var(--white) solid;
        border-top-right-radius: 20px;
        border-top-left-radius: 20px;
        height: 100%;
        width: 90%;
        margin: 0 auto;
        display: flex;
        flex-direction: column;
        justify-content: center;
        box-shadow: inset 0px 0px 15px 0px var(--black);
        background-color: var(--darkgrey);
        .footerLogos {
            width: 100%;
            margin-bottom: 3em;
            display: flex;
            justify-content: center;
            a {
                width: fit-content;
                display: flex;
                flex-direction: row;
                justify-content: center;
                img {
                    height: 2rem;
                }
                .performanteText {
                    filter: invert(99%) sepia(100%) saturate(6%) hue-rotate(108deg) brightness(102%) contrast(103%);
                }
            }
            @media screen and (max-width: 400px) {
                & {
                    a {
                        flex-direction: column;
                        padding: 0em 25%;
                        img {
                            height: 100%;
                        }
                    }
                }
            }
        }
        .socialMediaContainer {
            display: flex;
            justify-content: center;
            flex-direction: row;
            gap: 2em;
            position: relative;
            width: 100%;
            .footerIcon {
                filter: invert(100%) sepia(100%) saturate(2%) hue-rotate(88deg) brightness(106%) contrast(101%);
                font-size: 2.5rem;
                height: 3.3rem;
                .fa-brands {
                    transform: translateY(1em);
                    opacity: 0;
                    transition: 1.5s ease-out;
                }
            }
            .Discord {
                filter: invert(42%) sepia(58%) saturate(5089%) hue-rotate(357deg) brightness(89%) contrast(81%);
            }
            @media screen and (max-width: 800px) {
                & {
                    gap: 0.5em;
                    .footerIcon {
                        height: 1.5rem;
                        font-size: 1.6rem;
                    }
                }
            }
        }
        .terms {
            display: flex;
            width: 100%;
            justify-content: center;
            margin: 3em 0em;
            gap: 2rem;
            @media screen and (max-width: 500px) {
                gap: 0.5em;
            }
            .links {
                a {
                    text-decoration: none;
                    color: var(--white);
                    &:hover {
                        text-decoration: underline;
                    }
                }
            }
            .termsOfService {
                position: relative;
            }
            @media screen and (max-width: 400px) {
                flex-direction: column;
                text-align: center;
            }
        }
    }
}
