        width: 100%;
        height: fit-content;
        margin-top: 4em;
        @media screen and (max-width: 1000px) {
            & {
                margin-top: 3em;
            }
        }
        .textContainer {
            width: 100%;
            height: fit-content;
            overflow: hidden;
            .crosspageText {
                position: relative;
                font-family: 'Rowdies', serif;
                color: rgb(14, 14, 14);
                font-size: 40vmin;
                text-shadow: 1px 1px rgb(108, 108, 108), -1px -1px rgb(108, 108, 108);
                cursor: default;
                @media screen and (max-width: 1000px) {
                    & {
                        font-size: 20vmin;
                    }
                }
            }
        }
    }
    .visionContentContainer {
        color: var(--white);
        height: 100vh;
        display: flex;
        .visonTextContainer {
            width: 100%;
            height: 100vh;
            padding: 0em 10%;
            text-align: left;
            display: flex;
            flex-direction: column;
            justify-content: center;
            h3 {
                font-size: 5vmax;
                font-family: var(--secondaryHeaderFontFamily);
            }
            p {
                font-family: var(--paragraphFontFamily);
                font-size: 1.2rem;
                span {
                    font-weight: bolder;
                    text-shadow: 1px 1px 1px var(--red), -1px -1px 1px var(--red);
                }
            }
        }
        .visionPhoneContainer {
            width: 100%;
            height: 100%;
            .phoneListContainer {
                display: flex;
                transform: rotate(30deg);
                width: 100%;
                height: 100%;
                .phoneContainer {
                    height: 100%;
                    width: 100%;
                    .phoneListTwo {
                        transform: translateY(-500px);
                    }
                }
            }
        }
        @media screen and (max-width: 1000px) {
            & {
                flex-direction: column;
                margin-top: 10%;
                .visonTextContainer {
                    width: 80%;
                    padding: 2em 10%;
                    margin-bottom: 15%;
                }
            }
        }
        @media screen and (max-width: 500px) {
            .visonTextContainer {
                width: 80%;
                padding: 2em 10%;
                margin-bottom: -15%;
            }
        }
    }
}

