.ecosystemListContainer {
    width: 100%;
    height: 100%;
    .contentContainer {
        display: flex;
        justify-content: center;
        flex-direction: column;
        width: 100%;
        gap: 2em;
        padding: 3rem;
        height: 100%;
        background: linear-gradient(0deg, rgba(23,23,23,0.9) 0%, rgba(0,0,0,1) 100%);
        min-height: 100vh;
        @media screen and (max-width: 450px) {
            padding: 0.5rem;
        }
        .heading {
            display: flex;
            justify-content: center;
            flex-direction: column;
            width: 100%;
            color: var(--white);
            margin: 0 auto;
            text-align: center;
            h1 {
                font-size: 9vmin;
                position: relative;
                margin-bottom: 0.5rem;
                .LE {
                    text-shadow: 2px 2px 1px var(--red), -2px -2px 1px var(--red);
                }
                .two {
                    color: var(--red);
                    text-shadow: 1px 1px 1px var(--white), -1px -1px 1px var(--white);
                }
                &:after {
                    content: "";
                    position: absolute;
                    bottom: 0;
                    left: 0;
                    right: 0;
                    width: 100%;
                    height: 2px;
                    background-color: var(--red);
                    border-radius: 50%;
