            }
        }
        .linkContainer {
            display: flex;
            justify-content: center;
            flex-direction: column;
            width: 100%;
            gap: 1.5rem;
            a {
                width: fit-content;
                margin: 0 auto;
                button {
                    width: 350px;
                    font-family: var(--paragraphFontFamily);
